custom_css:
/*Typography*/

.is-layout-flow > * {
  margin-block-start: 1rem;
}

.wp-block-separator {
	border-top: 1px solid;
}

/* title of page */

.header .header__brand{
  font-size: 1rem;
}

.header .header__brand,
.menu-main-container,
.entry-header h1.entry-title,
.single .single-post__title h1 {
  margin: 0 0 1rem 0;
	line-height: 1.5rem;
}

/* header */

.header__col-left,
.menu-main-container {
  min-width: 226px;
}

.header__col-left,
.menu-main-container,
.entry-header h1.entry-title,
.single .single-post__title h1 {
  border-bottom: 1px solid;
}


.menu-main-container,
.header__secondary-menu-normal,
.single .single-post__title h1,
.entry-title {
  padding: 0 0 1rem 0;
  text-align: left;
}

.vertical .header .header__col-right .header__menu .nav__link,
.vertical .header .header__col-right .header__secondary__menu .nav__link {
  padding: 0;
}

.vertical .header .header__col-right div.menu-main-container {
  margin: 0 0 1rem 0;
}

/* hide entry title on homepage */

.home .entry-title {
  display: none;
}

/* portfolio postcards */

.post-card__heading {
  font-size: 1rem;
}

/* mobile */

@media (min-width: 769px) {
  .header__container {
    padding-right: 0;
  }
  .vertical .header .header__col-right div.menu-main-container {
    margin: 0;
  }
}

@media (max-width: 768px) {
  .header__menu {
    flex-direction: column;
  }

  .menu-main-container {
    padding-top: 1rem;
    margin: 0;
  }

  .content-area {
    padding-top: 0;
  }

  .site {
    display: block;
  }

  .header__col-left,
  .header .header__col-right,
  .menu-main-container {
    width: 100% !important;
  }

  .site-content {
    padding-top: 0;
  }
}
